import Cookies from 'js-cookie';
export default {
  data: function data() {
    return {
      visible: false,
      url: '',
      fileList: []
    };
  },
  methods: {
    init: function init() {
      this.visible = true;
      this.url = "".concat(window.SITE_CONFIG['apiURL'], "/act/process/deploy?token=").concat(Cookies.get('token'));
      this.fileList = [];
    },
    // 上传之前
    beforeUploadHandle: function beforeUploadHandle(file) {
      if (!/.+\.zip$/.test(file.name) && !/.+\.xml$/.test(file.name) && !/.+\.bar$/.test(file.name) && !/.+\.bpmn$/.test(file.name)) {
        this.$message.error(this.$t('upload.tip', {
          'format': 'zip、xml、bar、bpmn'
        }));
        return false;
      }
    },
    // 上传成功
    successHandle: function successHandle(res, file, fileList) {
      var _this = this;

      if (res.code !== 0) {
        return this.$message.error(res.msg);
      }

      this.$message({
        message: this.$t('prompt.success'),
        type: 'success',
        duration: 500,
        onClose: function onClose() {
          _this.visible = false;

          _this.$emit('refreshDataList');
        }
      });
    }
  }
};
